import React, { useState } from 'react';
import { Container, Form, Button, Alert } from 'react-bootstrap';
import ReCAPTCHA from 'react-google-recaptcha';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Registration = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    dateOfBirth: '',
    password: '',
    confirmPassword: '',
    bloodType: '',
    address: '',
    termsAccepted: false,
  });
  const [errors, setErrors] = useState({});
  const [recaptchaToken, setRecaptchaToken] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const validateForm = () => {
    const newErrors = {};

    // Email Validation
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Invalid email format.';
    }

    // Password Validation
    if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@#$%])[A-Za-z\d@#$%]{8,12}$/.test(formData.password)) {
      newErrors.password = 'Password must be 8-12 characters long and include uppercase, lowercase, numbers, and special characters (@, #, $, %).';
    }

    // Confirm Password Validation
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match.';
    }

    // Name Validation
    if (!/^[A-Za-z]{2,50}$/.test(formData.firstName)) {
      newErrors.firstName = 'First name must contain only letters and be 2-50 characters long.';
    }
    if (!/^[A-Za-z]{2,50}$/.test(formData.lastName)) {
      newErrors.lastName = 'Last name must contain only letters and be 2-50 characters long.';
    }

    // Phone Number Validation
    if (formData.phoneNumber && !/^\+\d{10,15}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Invalid phone number. Use format +1234567890.';
    }

    // Date of Birth Validation
    const dob = new Date(formData.dateOfBirth);
    const today = new Date();
    const age = today.getFullYear() - dob.getFullYear();
    if (age < 18 || isNaN(dob.getTime())) {
      newErrors.dateOfBirth = 'You must be at least 18 years old to register.';
    }

    // Terms & Conditions Validation
    if (!formData.termsAccepted) {
      newErrors.termsAccepted = 'You must accept the terms and conditions.';
    }

    // CAPTCHA Validation
    if (!recaptchaToken) {
      newErrors.recaptcha = 'Please complete the CAPTCHA.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const response = await api.post('/auth/signup', {
        ...formData,
        recaptchaToken,
      });
      alert('Registration successful! Please check your email for verification.');
      navigate('/sign-in');
    } catch (error) {
      console.error('Registration error:', error);
      alert('Error during registration. Please try again.');
    }
  };

  return (
    <Container>
      <h2>Donor Registration</h2>
      <Form onSubmit={handleSubmit}>
        {/* First Name */}
        <Form.Group>
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            isInvalid={!!errors.firstName}
          />
          <Form.Control.Feedback type="invalid">{errors.firstName}</Form.Control.Feedback>
        </Form.Group>

        {/* Last Name */}
        <Form.Group>
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            isInvalid={!!errors.lastName}
          />
          <Form.Control.Feedback type="invalid">{errors.lastName}</Form.Control.Feedback>
        </Form.Group>

        {/* Email */}
        <Form.Group>
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            isInvalid={!!errors.email}
          />
          <Form.Control.Feedback type="invalid">{errors.email}</Form.Control.Feedback>
        </Form.Group>

        {/* Password */}
        <Form.Group>
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            isInvalid={!!errors.password}
          />
          <Form.Control.Feedback type="invalid">{errors.password}</Form.Control.Feedback>
        </Form.Group>

        {/* Confirm Password */}
        <Form.Group>
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            isInvalid={!!errors.confirmPassword}
          />
          <Form.Control.Feedback type="invalid">{errors.confirmPassword}</Form.Control.Feedback>
        </Form.Group>

        {/* Phone Number */}
        <Form.Group>
          <Form.Label>Phone Number (Optional)</Form.Label>
          <Form.Control
            type="tel"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            isInvalid={!!errors.phoneNumber}
          />
          <Form.Control.Feedback type="invalid">{errors.phoneNumber}</Form.Control.Feedback>
        </Form.Group>

        {/* Date of Birth */}
        <Form.Group>
          <Form.Label>Date of Birth</Form.Label>
          <Form.Control
            type="date"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            isInvalid={!!errors.dateOfBirth}
          />
          <Form.Control.Feedback type="invalid">{errors.dateOfBirth}</Form.Control.Feedback>
        </Form.Group>

        {/* Blood Type */}
        <Form.Group>
          <Form.Label>Blood Type (Optional)</Form.Label>
          <Form.Control
            as="select"
            name="bloodType"
            value={formData.bloodType}
            onChange={handleChange}
          >
            <option value="">Select Blood Type</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
          </Form.Control>
        </Form.Group>

        {/* Terms & Conditions */}
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="I agree to the terms and conditions."
            name="termsAccepted"
            checked={formData.termsAccepted}
            onChange={handleChange}
            isInvalid={!!errors.termsAccepted}
          />
          <Form.Control.Feedback type="invalid">{errors.termsAccepted}</Form.Control.Feedback>
        </Form.Group>

        {/* CAPTCHA */}
        <ReCAPTCHA
          sitekey="YOUR_RECAPTCHA_SITE_KEY"
          onChange={(token) => setRecaptchaToken(token)}
        />
        {errors.recaptcha && <Alert variant="danger">{errors.recaptcha}</Alert>}

        {/* Submit Button */}
        <Button type="submit" variant="danger" className="mt-3">
          Register
        </Button>
      </Form>
    </Container>
  );
};

export default Registration;
