// src/components/Profile.js
import React, { useState, useEffect } from 'react';
import { Container, Table, Button } from 'react-bootstrap';
import api from '../utils/api';

const Profile = () => {
  const [donations, setDonations] = useState([]);
  const [donationId, setDonationId] = useState('');

  useEffect(() => {
    const fetchDonations = async () => {
      try {
        const response = await api.get('/donations');
        setDonations(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchDonations();
  }, []);

  const handleDonationIdSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/donations/update', { donationId });
      alert('Donation ID updated successfully!');
    } catch (error) {
      alert('Error updating donation ID.');
      console.error(error);
    }
  };

  return (
    <Container>
      <h2>Your Profile</h2>
      <h3>Donation History</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Donation ID</th>
            <th>Date</th>
            <th>Type</th>
            <th>Hospital</th>
          </tr>
        </thead>
        <tbody>
          {donations.map((donation) => (
            <tr key={donation.donationId}>
              <td>{donation.donationId}</td>
              <td>{donation.donationDate}</td>
              <td>{donation.donationType}</td>
              <td>{donation.hospitalName}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h3>Update Donation ID</h3>
      <form onSubmit={handleDonationIdSubmit}>
        <input
          type="text"
          placeholder="Enter Donation ID"
          value={donationId}
          onChange={(e) => setDonationId(e.target.value)}
          required
        />
        <Button type="submit" variant="danger">Update</Button>
      </form>
    </Container>
  );
};

export default Profile;
