// src/components/DonateBlood.js
import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const DonateBlood = () => {
  const [formData, setFormData] = useState({
    zipCode: '',
    country: '',
    donationType: '',
    startDate: '',
    endDate: '',
    distance: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/donations/schedule', formData);
      alert('Appointment scheduled successfully!');
      navigate('/profile');
    } catch (error) {
      alert('Error scheduling appointment.');
      console.error(error);
    }
  };

  return (
    <Container>
      <h2>Schedule a Blood Donation</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Zip Code</Form.Label>
          <Form.Control type="text" name="zipCode" onChange={handleChange} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Country</Form.Label>
          <Form.Control type="text" name="country" onChange={handleChange} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Donation Type</Form.Label>
          <Form.Control as="select" name="donationType" onChange={handleChange} required>
            <option value="whole-blood">Whole Blood</option>
            <option value="plasma">Plasma</option>
            <option value="platelets">Platelets</option>
          </Form.Control>
        </Form.Group>
        <Form.Group>
          <Form.Label>Start Date</Form.Label>
          <Form.Control type="date" name="startDate" onChange={handleChange} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>End Date</Form.Label>
          <Form.Control type="date" name="endDate" onChange={handleChange} required />
        </Form.Group>
        <Form.Group>
          <Form.Label>Distance (in miles)</Form.Label>
          <Form.Control type="number" name="distance" onChange={handleChange} required />
        </Form.Group>
        <Button type="submit" variant="danger">Search</Button>
      </Form>
    </Container>
  );
};

export default DonateBlood;
